import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from 'react-bootstrap';
import { Row, Button, Card } from 'react-bootstrap';
import { SearchBar } from './components/SearchBar';
import { Link, useNavigate } from 'react-router-dom';

function App() {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  const clickViewMore = () => {
    navigate('/BookListingInformation', {
      state:
      {
        BookID: item.BookID, Title: item.Title, Price: item.Price,
        Description: item.Description, Image: item.Image,
        GenreID: item.GenreID, LocationID: item.LocationID
      }
    })
  };

  useEffect(() => {
    fetch('/apis/book/get_all_books')
      .then(res => res.json())
      .then(data => {
        setIsLoaded(true);
        setItems(data);

      },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])


  return (
    <div className="App">
      <header className="App-header">
        <Container>
          <SearchBar />
          <div class="col d-flex justify-content-start mb-2">
            <Row><h3><b>Books For Enlightenment</b></h3></Row>
          </div>
          <div class="d-flex justify-content-center">
            <Row>
              {items.map(item => (
                <div class="col-sm mb-2" key={item.BookID}>
                  <Card style={{ width: '15rem', height: '28rem' }}>
                    <Card.Img variant="top" src={item.Image} style={{ height: '15rem' }} fluid={true} />
                    <Card.Body>
                      <Card.Title>{item.Title}</Card.Title>
                      {item.Price == 0 ? <Card.Text>Free</Card.Text> : null}
                      {item.Price > 0 ? <Card.Text>${item.Price}</Card.Text> : null}
                      <Card.Text>New</Card.Text>
                      <Link
                        to={{
                          pathname: "/BookListingInformation",
                          state: [{
                            BookID: item.BookID, Title: item.Title, Price: item.Price,
                            Description: item.Description, Image: item.Image,
                            GenreID: item.GenreID, LocationID: item.LocationID
                          }]
                        }}
                      >
                        <Button>View More</Button>
                      </Link>
                    </Card.Body>
                  </Card>
                </div>
              ))}
            </Row>
          </div>
          <div class="col d-flex justify-content-start mb-2">
            <Row><h3><b>Night Time Reads</b></h3></Row>
          </div>
        </Container>
      </header>
    </div>
  );
}

export default App;
