import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import { Button, Container, Form, Row, Col, Card } from "react-bootstrap"
import { SearchBar } from "../components/SearchBar";

export function SearchResult() {

    const location = useLocation();

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);

    const [searchResult, setSearchResult] = useState([]);

    function bookByCategories(BookGenre) {
        return (
            <>
                <div className="col d-flex justify-content-start mb-2">
                    <Row><h3><b>{BookGenre}</b></h3></Row>
                </div>
                <div className="d-flex justify-content-center">
                    <Row xs="auto">
                        {items.map(item => (
                            <div className="col-sm mb-2" key={item.BookID}>
                                {item.Genre === BookGenre ?
                                    <Card style={{ width: '15rem', height: '28rem' }}>
                                        <Card.Img variant="top" src={item.Image} style={{ height: '15rem' }} />
                                        <Card.Body>
                                            <Card.Title>{item.Title}</Card.Title>
                                            {item.Price === 0 ? <Card.Text>Free</Card.Text> : null}
                                            {item.Price > 0 ? <Card.Text>${item.Price}</Card.Text> : null}
                                            <Card.Text>{item.BookCondition}</Card.Text>
                                            <Button onClick={() => {
                                                navigate('/BookListingInformation', {
                                                    state: {
                                                        BookID: item.BookID,
                                                        Condition: item.BookCondition,
                                                        Title: item.Title,
                                                        Price: item.Price,
                                                        Description: item.Description,
                                                        Image: item.Image,
                                                        Genre: item.Genre,
                                                        Location: item.Location,
                                                        Route: "Home"
                                                    }
                                                });
                                            }}>View More</Button>
                                        </Card.Body>
                                    </Card> : null}
                            </div>
                        ))}
                    </Row>
                </div>
            </>
        )
    }

    useEffect(() => {
        console.log(location.state.SearchKey)
        if (location.state.SearchKey !== null) {
            fetch('/apis/book/search_book_by_title?Title=' + location.state.SearchKey)
                .then(res => res.json())
                .then(data => {
                    setIsLoaded(true);
                    setSearchResult(data);
                },
                    (error) => {
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }
    }, [])

    return (
        <Container>
            <SearchBar />
            <h3>Search Result for '{location.state.SearchKey}'</h3>
            <Row xs="auto" className="mt-4">
                <Col><h5>Filter:</h5></Col>
                <Col><Button>Genre</Button></Col>
                <Col><Button>Location</Button></Col>
                <Col><Button>Price</Button></Col>
                <p>{searchResult.BookID}</p>
            </Row>
        </Container>
    )
}
