import React, { useState, useEffect } from 'react'
import { Container, Row } from 'react-bootstrap'
import Button from 'react-bootstrap/Button'
import Card from 'react-bootstrap/Card'
import 'bootstrap/dist/css/bootstrap.min.css';
import { LinkContainer } from 'react-router-bootstrap'
import { useNavigate } from 'react-router-dom';

function MyListings() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetch('/apis/book/get_all_books')
            .then(res => res.json())
            .then(data => {
                setIsLoaded(true);
                setItems(data);

            },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )
    }, [])

    return (
        <div className='mt-4 d-flex justify-content-center'>
            <Container>
                <div class="d-flex justify-content-center">
                    <LinkContainer to="/MyListings/NewListings">
                        <Button> Create New Listings</Button>
                    </LinkContainer>
                </div>
                <div className='mb-4'>
                    <h3>My Listings</h3>
                </div>
                <div class="d-flex justify-content-center">
                    <Row>
                        {items.map(item => (
                            <div className="col-sm mb-2" key={item.BookID}>
                                <Card style={{ width: '15rem', height: '28rem' }}>
                                    <Card.Img variant="top" src={item.Image} style={{ height: '15rem' }} />
                                    <Card.Body>
                                        <Card.Title>{item.Title}</Card.Title>
                                        {item.Price === 0 ? <Card.Text>Free</Card.Text> : null}
                                        {item.Price > 0 ? <Card.Text>${item.Price}</Card.Text> : null}
                                        <Card.Text>New</Card.Text>
                                        <Button onClick={() => {
                                            navigate('/BookListingInformation', {
                                                state: {
                                                    BookID: item.BookID, Title: item.Title, Price: item.Price,
                                                    Description: item.Description, Image: item.Image,
                                                    Genre: item.Genre, Location: item.Location
                                                }
                                            });
                                        }}>View More</Button>
                                    </Card.Body>
                                </Card>
                            </div>
                        ))}
                    </Row>
                </div>
            </Container>
        </div>
    )
};

export default MyListings;
