import React, { useState, useEffect } from 'react'
import { Container } from 'react-bootstrap'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import FloatingLabel from 'react-bootstrap/FloatingLabel'

function NewListings() {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [genre, setGenre] = useState([]);
    const [location, setLocation] = useState([]);

    const [BookTitle, setBookTitle] = useState();
    const [Price, setPrice] = useState();
    const [Description, setDescription] = useState();
    const [Condition, setCondition] = useState();
    const [GenreID, setGenreID] = useState();
    const userEmail = localStorage.getItem('Email');
    const [BookImage, setBookImage] = useState({});
    const [locationID, setLocationID] = useState();

    const uploadedImage = (e) => {
        console.log(e.target.files[0]);
        const formData = new FormData();
        formData.append('BookImage', e.target.files[0]);
        console.log(formData);
        setBookImage(formData);
    };

    useEffect(() => {
        fetch('/apis/book/get_all_genres')
            .then(res => res.json())
            .then(data => {
                setIsLoaded(true);
                setGenre(data);
            },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )

        fetch('/apis/book/get_all_locations')
            .then(res => res.json())
            .then(data => {
                setIsLoaded(true);
                setLocation(data);
            },
                (error) => {
                    setIsLoaded(true);
                    setError(error);
                }
            )

    }, [])

    const postNewListings = async (e) => {

        e.preventDefault();

        console.log("BookTitle:" + BookTitle);
        console.log("Condition:" + Condition);
        console.log("GenreID:" + GenreID);
        console.log("Description:" + Description)
        console.log("BookImage:" + BookImage);
        console.log("LocationID:" + locationID);
    }

    return (
        <Container>
            <div className="d-flex justify-content-center">
                <h1>New Listings</h1>
            </div>
            <div className="d-flex justify-content-center">
                <Form onSubmit={postNewListings}>
                    <Form.Group className="mb-3" controlId="formBookName">
                        <Form.Label>Book Title:</Form.Label>
                        <Form.Control required type="text" placeholder="Enter book name" value={BookTitle} onChange={e => setBookTitle(e.target.value)} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formGenre">
                        <Form.Label>Genre:</Form.Label>
                        <Form.Select required aria-label="Floating label select condition">
                            {Array.isArray(genre) ?
                                genre.map(genre => (
                                    <option value={genre.GenreID} onChange={e => setGenreID(e.target.value)}>{genre.GenreName}</option>)) : null
                            }
                        </Form.Select>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formCondition">
                        <FloatingLabel controlId="floatingSelectCondition" label="Book Condition">
                            <Form.Select required aria-label="Floating label select condition" value={Condition} onSelect={e => setCondition(e.target.value)}>
                                <option value="New">New</option>
                                <option value="Well Used">Well Used</option>
                            </Form.Select>
                        </FloatingLabel>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBookPrice">
                        <Form.Label>Book Price:</Form.Label>
                        <Form.Control required type="text" placeholder="Enter book price" value={Price} onChange={e => setPrice(e.target.value)} />
                        <Form.Text className="text-muted">
                            Enter 0 if you wish to give the book out.
                        </Form.Text>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBookDescription">
                        <Form.Label>Book Description:</Form.Label>
                        <Form.Control required as="textarea" rows={3} value={Description} onChange={e => setDescription(e.target.value)} />
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formBookImage">
                        <Form.Label>Book Image: </Form.Label>
                        <Form.Control required type="file" onChange={e => uploadedImage(e)} />
                        <Form.Text className="text-muted">Upload Profile Picture (image format must be png, jpg, or jpeg).</Form.Text>
                    </Form.Group>
                    <Form.Group className="mb-3" controlId="formLocation">
                        <FloatingLabel controlId="floatingSelectLocation" label="Location">
                            <Form.Select aria-label="Floating label select location">
                                {Array.isArray(location) ?
                                    location.map(location => (
                                        <option value={location.LocationID} onChange={e => setLocationID(e.target.value)}>{location.LocationName}</option>)) : null
                                }
                            </Form.Select>
                        </FloatingLabel>
                    </Form.Group>
                    <div className="d-flex justify-content-center mb-3">
                        <Button variant="primary" type="submit">
                            Upload
                        </Button>
                    </div>
                </Form>
            </div>
        </Container >
    );
};

export default NewListings;
