import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from 'react-bootstrap';
import { Row, Button, Card } from 'react-bootstrap';
import { SearchBar } from './components/SearchBar';
import { useNavigate } from 'react-router-dom';

function App() {

  const [error, setError] = useState(null);
  const [isLoaded, setIsLoaded] = useState(false);
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch('/apis/book/get_all_books')
      .then(res => res.json())
      .then(data => {
        setIsLoaded(true);
        setItems(data);

      },
        (error) => {
          setIsLoaded(true);
          setError(error);
        }
      )
  }, [])


  return (
    <div className="App">
      <header className="App-header">
        <Container>
          <SearchBar />
          <div className="col d-flex justify-content-start mb-2">
            <Row><h3><b>Fiction</b></h3></Row>
          </div>
          <div className="d-flex justify-content-center">
            <Row>
              {items.map(item => (
                <div className="col-sm mb-2" key={item.BookID}>
                  {item.Genre === 'Fiction' ?
                    <Card style={{ width: '15rem', height: '28rem' }}>
                      <Card.Img variant="top" src={item.Image} style={{ height: '15rem' }} />
                      <Card.Body>
                        <Card.Title>{item.Title}</Card.Title>
                        {item.Price === 0 ? <Card.Text>Free</Card.Text> : null}
                        {item.Price > 0 ? <Card.Text>${item.Price}</Card.Text> : null}
                        <Card.Text>New</Card.Text>
                        <Button onClick={() => {
                          navigate('/BookListingInformation', {
                            state: {
                              BookID: item.BookID, Title: item.Title, Price: item.Price,
                              Description: item.Description, Image: item.Image,
                              Genre: item.Genre, Location: item.Location, Route: "Home"
                            }
                          });
                        }}>View More</Button>
                      </Card.Body>
                    </Card> : null}
                </div>
              ))}
            </Row>
          </div>
          <div className="col d-flex justify-content-start mb-2">
            <Row><h3><b>Night Time Reads</b></h3></Row>
          </div>
          <div className="d-flex justify-content-center">
            <Row>
              {items.map(item => (
                <div className="col-sm mb-2" key={item.BookID}>
                  {item.Genre === 'Non-Fiction' ?
                    <Card style={{ width: '15rem', height: '28rem' }}>
                      <Card.Img variant="top" src={item.Image} style={{ height: '15rem' }} />
                      <Card.Body>
                        <Card.Title>{item.Title}</Card.Title>
                        {item.Price === 0 ? <Card.Text>Free</Card.Text> : null}
                        {item.Price > 0 ? <Card.Text>${item.Price}</Card.Text> : null}
                        <Card.Text>New</Card.Text>
                        <Button onClick={() => {
                          navigate('/BookListingInformation', {
                            state: {
                              BookID: item.BookID, Title: item.Title, Price: item.Price,
                              Description: item.Description, Image: item.Image,
                              Genre: item.Genre, Location: item.Location, Route: "Home"
                            }
                          });
                        }}>View More</Button>
                      </Card.Body>
                    </Card> : null}
                </div>
              ))}
            </Row>
          </div>
        </Container>
      </header>
    </div>
  );
}

export default App;
