import React, { useState } from 'react';
import { Button, Modal, Form, Row, Col } from 'react-bootstrap';
import { useLocation, useNavigate } from 'react-router-dom';

export default function OfferBookModal() {
    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const location = useLocation();
    const navigate = useNavigate();

    const TransactionData = new FormData();
    const [Price, setPrice] = useState();

    const [errorMessages, setErrorMessages] = useState([]);
    const [showErrors, setShowErrors] = useState(false);

    let errors = [];

    const postTransaction = async (e) => {

        e.preventDefault();

        const userEmail = localStorage.getItem('Email');
        const book_id = location.state.BookID;

        console.log(book_id);
        console.log(userEmail);
        console.log(Price);

        // DeleteBookData.append('BookID', book_id);
        // DeleteBookData.append('Email', userEmail);

        // const res = await fetch('/apis/book/delete_book', {
        //     method: "POST",
        //     body: DeleteBookData
        // });

        // const data = await res.json();

        // if (res.status === 201) {

        //     const trimmedResponseMessage = JSON.stringify(data).replace(/[^a-zA-Z ]/g, "");

        //     if (trimmedResponseMessage === "Error in deleting book") {
        //         errors.push(trimmedResponseMessage);
        //         setShowErrors({ showErrors: true });
        //         setErrorMessages(errors);
        //     }
        //     else {
        //         navigate('/');
        //         window.location.reload(false);
        //     }
        // }
    }

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Offer
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Are you sure you want to offer for this listing?</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form>
                        <Form.Group as={Row} className="mb-3" controlId="formPlaintextEmail">
                            <Form.Label column sm="1" xs lg="2">
                                <span className="material-symbols-outlined">
                                    attach_money
                                </span>
                            </Form.Label>
                            <Col xs={5}>
                                <Form.Control placeholder="Price" type="text" value={Price} onChange={e => setPrice(e.target.value)} />
                            </Col>
                        </Form.Group>
                    </Form>
                    <p>Enter 0 if you wish to have the book for free</p>
                    {showErrors ? errorMessages.map((item, index) => {
                        return <ul key={index}>{item}</ul>;
                    }) : null}
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="danger" onClick={postTransaction}>
                        Confirm
                    </Button>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
