import React, { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import { Row, Col } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const navigate = useNavigate();
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const [email, setEmail] = useState();
    const [password, setPassword] = useState();

    const setData = async (e) => {
        e.preventDefault()

        const res = await fetch('/apis/user/login', {
            method: "POST",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                "Email": email,
                "Password": password
            })

        });

        const data = await res.json();

        if (res.status === 201) {
            window.alert("Login Successful")
            navigate('/')
        }

        else {
            window.alert('Login Failed')
        }


    }
    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Login
            </Button>

            <Modal show={show} centered onHide={handleClose} size="xl">
                <Modal.Header closeButton>
                    <Modal.Title><h1>Login</h1></Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div class="justify-content-center">
                        <Form className="mb-3" method="POST">
                            <Row className="ms-5">
                                <Form.Group as={Row} className="mb-3" controlId="formEmail">
                                    <Form.Label column sm="1" xs lg="1">
                                        <span class="material-symbols-outlined">
                                            mail
                                        </span>
                                    </Form.Label>
                                    <Col xs={9}>
                                        <Form.Control type="text" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
                                    </Col>
                                </Form.Group>
                            </Row>
                            <Row className="ms-5">
                                <Form.Group as={Row} className="mb-3" controlId="formPassword">
                                    <Form.Label column sm="1" xs lg="1">
                                        <span class="material-symbols-outlined">
                                            lock
                                        </span>
                                    </Form.Label>
                                    <Col xs={9}>
                                        <Form.Control type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
                                    </Col>
                                </Form.Group>
                            </Row>
                            <div class="d-flex justify-content-center">
                                <Button variant="primary" onClick={handleClose, setData} type="submit">
                                    Login
                                </Button>
                            </div>
                        </Form>
                    </div>
                    <div class="d-flex justify-content-center">
                        <LinkContainer to="/ForgetPassword">
                            <a onClick={handleClose} href="/ForgetPassword">Forget Password?</a>
                        </LinkContainer>
                    </div>
                </Modal.Body>
            </Modal>
        </>
    );
};

export default Login;
