import React, { useState } from 'react'
import { Container } from 'react-bootstrap'
import { Row, Col, Button, Alert } from 'react-bootstrap'
import { useLocation, useNavigate } from 'react-router-dom'
import { SearchBar } from '../components/SearchBar'
import LoginAlert from '../components/LoginAlert'

function BookListingInformation() {
    const Authentication = localStorage.getItem('Authentication');
    const location = useLocation();
    const navigate = useNavigate();

    function CheckLogin() {
        if (Authentication === null) {

        }
        else if (Authentication === 'true') {
            navigate('/');
        }
    }

    function AlertDismissibleExample() {
        const [show, setShow] = useState(true);

        if (show) {
            return (
                <Alert variant="danger" onClose={() => setShow(false)} dismissible>
                    <Alert.Heading>Oh snap! You got an error!</Alert.Heading>
                    <p>
                        Change this and that and try again. Duis mollis, est non commodo
                        luctus, nisi erat porttitor ligula, eget lacinia odio sem nec elit.
                        Cras mattis consectetur purus sit amet fermentum.
                    </p>
                </Alert>
            );
        }
        return <Button onClick={() => setShow(true)}>Show Alert</Button>;
    }

    return (
        <Container>
            <SearchBar />
            <div className="d-flex align-items-center justify-content-center mb-4">
                <img src={location.state.Image} className='img-fluid' alt={location.state.Title} />
            </div>
            <div className="row justify-content-center">
                <table className="ms-3">
                    <Row>
                        <Col className="mb-2 mt-2"><h4><b>{location.state.Title}</b></h4></Col>
                        {/* <Col md={{ span: 3, offset: 5 }} xs={5} className="mb-2 mt-2">Shawn</Col> */}
                    </Row>
                    <Row>
                        <Col md={4} xs={6} className="mb-2 mt-2">
                            {location.state.Price === 0 ? "Free" : null}
                            {location.state.Price > 0 ? "$" + location.state.Price : null}
                        </Col>
                        <Col style={{ justifyContent: 'end' }} md={{ span: 3, offset: 5 }} xs={5} className="mb-2 mt-2">
                            {location.state.Route === "MyListings" ? < Button variant='primary'> Edit Details</Button> : null}
                            {location.state.Route !== "MyListings" && Authentication === "true" ? <Button variant='primary' onClick={CheckLogin}> Offer</Button> : null}
                            {location.state.Route !== "MyListings" && Authentication === null ? <Button variant='primary' onClick={AlertDismissibleExample}>Offer</Button> : null}
                        </Col>
                    </Row>
                    <Row className="mb-2 mt-2">
                        <p>Used</p>
                    </Row>
                    <Row className="mb-2 mt-2">
                        <h5>Description</h5>
                    </Row>
                    <Row>
                        <p>{location.state.Description}</p>
                    </Row>
                    <Row className="mb-2 mt-4">
                        <h5>
                            Location
                        </h5>
                    </Row>
                    <Row className="mb-2 mt-2">
                        <p>
                            <span class="material-symbols-outlined">
                                location_on
                            </span>
                            {location.state.Location}
                        </p>
                    </Row>
                </table>
            </div>
        </Container >
    );
}

export default BookListingInformation;
